<?php

namespace Modules\Shop\tests\Feature;

use Illuminate\Support\Str;

class AdminProductManagementTest extends TestCase
{

    private string $adminToken;

    public function setUp(): void
    {
        parent::setUp();
        $this->adminToken = $this->adminRepository->createAccessToken($this->adminRepository->faker()->create()->id)->accessToken;
    }

    public function testProductCanBeCreatedByAdmin(): void
    {
        $category = $this->productCategoryRepository->faker()->create();
        $response = $this
            ->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->post(route('shop.admin.products.store' , [
                "name" => fake()->name,
                'product_product_category_id' => $category->id,
                "price" => rand(1000, 100000),
                "image" => "data:image/png;base64,",
                "description" => fake()->text(),
                "product_category_id" => $category->id
            ]));


        $response->assertStatus(201)->assertSee('data');
        $this->assertCount(1 , $this->productRepository->getAll());
        $this->assertSame($category->id , $this->productRepository->getAll()->first()->product_category_id);
    }

    public function testProductCantBeCreatedByGuest(): void
    {
        $category = $this->productCategoryRepository->faker()->create();
        $response = $this
            ->withHeaders($this->headers)
            ->post(route('shop.admin.products.store' , [
                "name" => fake()->name,
                'product_category_id' => $category->id,
                "price" => rand(1000, 100000),
                "image" => base64_encode(Str::random(50)),
                "description" => fake()->text(),
            ]));

        $response->assertStatus(401)->assertSee('message');
    }

    public function testProductCantBeCreatedByValidationNameError(): void
    {
        $category = $this->productCategoryRepository->faker()->create();
        $response = $this
            ->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->post(route('shop.admin.products.store' , [
                'product_category_id' => $category->id,
                "price" => rand(1000, 100000),
                "image" => "data:image/png;base64,",
                "description" => fake()->text(),
            ]));

        $response->assertJsonValidationErrors('name');
    }

    public function testProductCantBeCreatedByValidationCategoryIdError(): void
    {
        $category = $this->productCategoryRepository->faker()->create();
        $response = $this
            ->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->post(route('shop.admin.products.store' , [
                "name" => fake()->name,
                "price" => rand(1000, 100000),
                "image" => "data:image/png;base64,",
                "description" => fake()->text(),
                'product_category_id' => 5645645645
            ]));

        $response->assertJsonValidationErrors('product_category_id');
    }

    public function testProductCantBeCreatedByValidationPriceError(): void
    {
        $category = $this->productCategoryRepository->faker()->create();
        $response = $this
            ->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->post(route('shop.admin.products.store' , [
                "name" => fake()->name,
                'product_category_id' => $category->id,
                "price" => "sadasd",
                "image" => "data:image/png;base64,",
                "description" => fake()->text(),
            ]));

        $response->assertJsonValidationErrors('price');
    }

    public function testProductCantBeCreatedByValidationImageError(): void
    {
        $category = $this->productCategoryRepository->faker()->create();
        $response = $this
            ->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->post(route('shop.admin.products.store' , [
                "name" => fake()->name,
                'product_category_id' => $category->id,
                "price" => rand(1000, 100000),
                "description" => fake()->text(),
            ]));

        $response->assertJsonValidationErrors('image');
    }

    public function testProductIndexCanBeShown()
    {
        $this->productRepository->faker()->count(10)->create();

        $response = $this->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->get(route('shop.admin.products.index'));

        $response->assertStatus(200);
        $response->assertSee('data');
        $this->assertCount(10 , $response->json()['data']);
    }

    public function testProductIndexShouldBePaginated()
    {
        $this->productRepository->faker()->count(45)->create();

        $response = $this->withHeaders($this->headers + ['Authorization' => 'Bearer '.$this->adminToken])
            ->get(route('shop.admin.products.index'));

        $response->assertStatus(200);
        $response->assertSee('data');
        $response->assertSee('per_page');
        $response->assertSee('total');
        $this->assertCount(10 , $response->json()['data']);
        $this->assertEquals(45 , $response->json()['meta']['total']);
        $this->assertEquals(10 , $response->json()['meta']['per_page']);
    }

    public function testProductIndexCanBeShownToAdminOnly()
    {
        $this->productRepository->faker()->count(10)->create();

        $response = $this->withHeaders($this->headers)
            ->get(route('shop.admin.products.index'));

        $response->assertStatus(401);
        $response->assertSee('message');
    }
}

